import React from "react";
import { Formik } from "formik";
import { Link } from "react-router-dom";
import Imagen from "./../img/Obreros.jpg";

const Login = () => {
    
  return (
    <>
      <div className="form-container">
        <img src={Imagen} width="650px"></img>
        <Formik
            onSubmit={() => {
                console.log('formulario enviado')
            }}
        >
          {({values, handleChange}) => (
            <form className="form">
              <h1>Bienvenido, por favor ingresa tus datos</h1>
              <div>
                <input onChange={handleChange} type="text" placeholder="Email"></input>
              </div>
              <div>
                <input
                onChange={handleChange}
                  type={"password"}
                  placeholder="Contraseña"
                ></input>
              </div>
              <button onClick={() => console.log(values)}>Ingresar</button>
              <br />
              <Link className="link" to={"/register"}>
                Registrarse
              </Link>
            </form>
          )}
        </Formik>
      </div>
    </>
  );
};

export default Login;
